package uu;

public class logicaloperation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=100008;
		
		if(a<=100)
		{
			System.out.println("a is small no");
		}
		else if (a<=1000)
		{
			System.out.println("a is medium no");
		}
		else if (a<10001)
		{
			System.out.println("a is large");
		}
		else if (a<100001)
		{
			System.out.println("a is big no");
		}
		else
		{
			System.out.println("a biggest no");
		}

	}

}
